mapper.select:
SELECT * FROM ( SELECT ROWNUM NUM, N.* FROM ( SELECT A.P_CODE, A.P_NAME, A.P_ADDRESS, A.P_KIND_NAME, A.P_DEFAULT_TIME_MIN, A.P_DEFAULT_PRICE, A.P_ADD_PRICE_MIN, A.P_ADD_PRICE, B.P_TOTAL_SPACE, A.P_WEEK_OPEN_TIME, A.P_WEEK_END_TIME, A.P_WEEKEND_OPEN_TIME, A.P_WEEKEND_END_TIME, A.P_TEL FROM SEOULPARKING A INNER JOIN SEOULPARKING10 B ON A.P_CODE = B.P_CODE WHERE A.P_TOTAL_SPACE >= 10
GROUP BY A.P_CODE, A.P_NAME, A.P_ADDRESS, A.P_KIND_NAME, B.P_TOTAL_SPACE, A.P_TEL, A.P_WEEK_OPEN_TIME, A.P_WEEK_END_TIME, A.P_WEEKEND_OPEN_TIME, A.P_WEEKEND_END_TIME, A.P_DEFAULT_TIME_MIN, A.P_DEFAULT_PRICE, A.P_ADD_PRICE_MIN, A.P_ADD_PRICE ) N ORDER BY
N.P_TOTAL_SPACE DESC
N.P_DEFAULT_TIME_MIN DESC
N.P_CODE DESC
) WHERE NUM BETWEEN #{param1} AND #{param2}
